import React, { useState, useEffect } from "react";
import styled from "styled-components";

const InfiniteStories = props => {
  const [ids, setIds] = useState([]);
  const [stories, setStories] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [visible, setVisible] = useState([]);
  const [loadMore, setLoadMore] = useState(false);
  const [lastId, setLastId] = useState(0);

  useEffect(() => {
    getStories();
    setVisible(stories.slice(lastId, lastId + 30));
  }, []);

  const getStories = () => {
    fetch("https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty")
      .then(res => {
        return !res.ok ? res.json().then(e => Promise.reject(e)) : res.json();
      })
      .then(res => {
        setIds(res);
        return res;
      })
      .then(ids =>
        ids.map(id =>
          fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)
            .then(res => {
              return !res.ok
                ? res.json().then(e => Promise.reject(e))
                : res.json();
            })
            .then(res => {
              setStories(prevState => [...prevState, res]);
              return stories;
            })
            .catch(console.log)
        )
      )
      .catch(console.log);
  };

  console.log("Stories", stories);
  console.log("Visible Stories", visible);

  useEffect(() => {
    setVisible(stories.slice(lastId, lastId + 30));
    setLoadMore(false);
    loadMore && setLastId(lastId + 30);
  }, [loadMore]);

  const LoadMoreDiv = styled.div`
    background: linear-gradient(hsla(0, 0%, 100%, 0), #f9f8f6);
    height: 390px;
    margin-top: -370px;
    pointer-events: none;
    position: relative;
    text-align: center;
    z-index: 2;
  `;

  const Button = styled.div`
    bottom: 0;
    left: 50%;
    margin-left: -90px;
    pointer-events: auto;
    position: absolute;
    width: 180px;
    font-family: FuturaBT-Book, sans-serif;
    font-size: 18px;
    font-weight: 300;
    background: #ffb21a;
    border-radius: 52px;
    color: #fff;
    padding: 16px 20px 15px;
    text-decoration: none;
    transition: background 80ms linear, opacity 1s linear;
    white-space: nowrap;
  `;
  return (
    <LoadMoreDiv>
      <Button
        href="#"
        data-category="?category_id=money-diaries"
        onClik={() => setLoadMore(true)}
      >
        More posts
      </Button>
    </LoadMoreDiv>
  );
};

export default InfiniteStories;
