import React, { useState } from "react";
import styled from "styled-components";
import { HeaderLinks, Icons } from "./Constants";

const ToggleMenu = styled.nav`
  bottom: 0;
  height: 100vh;
  right: -100%;
  top: 0;
  width: 100%;
  z-index: 11;
  color: #fff;
  transition: all 0.5s ease;
  position: fixed;
  background: #fab131;
  ${props => props && `right: 0;`}
`;

const ToggleClose = styled.div`
  background: url(/assets/magazine/images/modules/cross-white-d52a75dâ€¦.svg)
    no-repeat;
  display: block;
  height: 22px;
  position: absolute;
  right: 25px;
  top: 25px;
  width: 22px;
  cursor: pointer;
  margin: auto;
`;
const ToggleMenuItems = styled.div`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  font-family: FuturaBT-Book, sans-serif;
  font-weight: 300;
  font-size: 14px;
  width: 100%;
  letter-spacing: 3px;
  text-align: center;
`;

const ToggleMenuMain = styled.div`
  margin: 0 0 60px;
`;

const ToggleButton = styled.a.attrs({
  className: "items-center flex relative cursor-pointer"
})`
  background-color: #fab131;
  border-radius: 50%;
  height: 32px;
  width: 32px;
`;
const ToggleButtonLines = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
`;
const ToggleButtonLine = styled.div`
  margin-bottom: 2px;
  width: 38%;
  height: 1px;
  background-color: #fff;
  margin: 0 auto 4px;
`;
const ToggleNavLink = styled.a`
  font-family: FuturaBT-Heavy, sans-serif;
  font-size: 14px;
  font-weight: 100;
  display: block;
  letter-spacing: 0.05em;
`;
const IconLi = styled.li`
  margin: 0 10px;
`;
const Icon = styled.a.attrs({
  className: "block overflow-hidden uppercase no-underline text-white"
})`
  background: ${props => props.url} no-repeat;
  line-height: 40px;
  height: 35px;
  text-indent: -9999em;
  width: 28px;
`;

const ToggleNav = () => {
  const [toggle, setToggle] = useState(false);
  return (
    <>
      {toggle && 
    <ToggleMenu toggle={toggle}>
    <div class="hamburger-button-close" data-hamburger-action="close"></div>

    <div class="hamburger-menu-items">
      <div class="hamburger-menu-items-main">
        <a href="https://www.wealthsimple.com/en-ca/details" class="hamburger-nav-link">The Details</a>
        <a class="hamburger-nav-link " href="/en-ca/magazine">Magazine</a>
        <a href="https://www.wealthsimple.com/en-ca/who-we-are" class="hamburger-nav-link">Who we are</a>
        
        <a href="https://my.wealthsimple.com/app/login" class="hamburger-nav-link">Sign in</a>
      </div>
      <div class="hamburger-menu-items-secondary">
        <a class="hamburger-nav-link" href="https://my.wealthsimple.com/signup">Start investing</a>
        <ul class="app-icons">
          <li class="app-icon app-icon-ios">
            <a href="https://wealthsimple.app.link/1nqeHghK4H" class="" title="Download on the App Store">Download on the App Store</a>
          </li>
          <li class="app-icon app-icon-android">
            <a href="https://wealthsimple.app.link/1nqeHghK4H" class="" title="Download on the App Store">Android app on Google Play</a>
          </li>
        </ul>
      </div>
    </div>
    </ToggleMenu>}
      <ToggleButton onClick={() => setToggle(!toggle)}>
        <ToggleButtonLines>
          <ToggleButtonLine />
          <ToggleButtonLine />
          <ToggleButtonLine />
        </ToggleButtonLines>
      </ToggleButton>
    </>
  );
};
export default ToggleNav;
