import React, { useState } from "react";
import styled from "styled-components";
import Media from "react-media";
import { HeaderLinks, ToggleLinks } from "./Constants";

const HeaderNavLink = styled.a.attrs({
  className: `no-underline`
})`
  margin-left: 20px;
  color: ${props =>
    props.active ? "#121212" : props.isButton ? "#fff" : "hsla(0,0%,7%,.5)"};
  font-size: 16px;
  @media (min-width: 1020px) {
    font-size: 18px;
  }
  ${props =>
    props.isButton &&
    `
    font-family: FuturaBT-Book,sans-serif
    font-weight: 300;
    background: #ffb21a;
    border-radius: 52px;
    padding: 16px 20px 15px;
    cursor : pointer;
    transition: background 80ms linear, opacity 1s linear;
    white-space: nowrap`}
`;
const ToggleMenu = styled.nav`
  bottom: 0;
  height: 100vh;
  right: -100%;
  top: 0;
  width: 100%;
  z-index: 11;
  color: #fff;
  transition: all 0.5s ease;
  position: fixed;
  background: #fab131;
`;

const ToggleClose = styled.div`
  background: url(/assets/magazine/images/modules/cross-white-d52a75dâ€¦.svg)
    no-repeat;
  display: block;
  height: 22px;
  position: absolute;
  right: 25px;
  top: 25px;
  width: 22px;
  cursor: pointer;
  margin: auto;
`;
const ToggleMenuItems = styled.div`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  font-family: FuturaBT-Book, sans-serif;
  font-weight: 300;
  font-size: 14px;
  width: 100%;
  letter-spacing: 3px;
  text-align: center;
`;

const ToggleMenuMain = styled.div`
  margin: 0 0 60px;
`;

const ToggleButton = styled.a.attrs({
  className: "items-center flex relative cursor-pointer"
})`
  background-color: #fab131;
  border-radius: 50%;
  height: 32px;
  width: 32px;
`;
const ToggleButtonLines = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
`;
const ToggleButtonLine = styled.div`
  margin-bottom: 2px;
  width: 38%;
  height: 1px;
  background-color: #fff;
  margin: 0 auto 4px;
`;
const ToggleNavLink = styled.a`
  font-family: FuturaBT-Heavy, sans-serif;
  font-size: 14px;
  font-weight: 100;
  display: block;
  letter-spacing: 0.05em;
`;

const Nav = () => (
  <nav className="lg:block md:block hidden">
    <ul className="flex justify-end">
      {HeaderLinks.map((link, index) => (
        <li key={index}>
          <HeaderNavLink
            active={link.active}
            href={link.href}
            isButton={link.isButton}
          >
            {link.name}
          </HeaderNavLink>
        </li>
      ))}
    </ul>
  </nav>
);

const ToggleNav = () => {
  const [toggle, setToggle] = useState(false);
  return (
    <>
      {toggle && (
        <ToggleMenu>
          <ToggleClose />

          <ToggleMenuItems>
            <ToggleMenuMain>
              {ToggleLinks.map((link, index) => (
                <ToggleNavLink href={link.value}>{link.text}</ToggleNavLink>
              ))}
            </ToggleMenuMain>
            <div class="hamburger-menu-items-secondary">
              <a
                class="hamburger-nav-link"
                href="https://my.wealthsimple.com/signup"
              >
                Start investing
              </a>
              <ul class="app-icons">
                <li class="app-icon app-icon-ios">
                  <a
                    href="https://wealthsimple.app.link/1nqeHghK4H"
                    class=""
                    title="Download on the App Store"
                  >
                    Download on the App Store
                  </a>
                </li>
                <li class="app-icon app-icon-android">
                  <a
                    href="https://wealthsimple.app.link/1nqeHghK4H"
                    class=""
                    title="Download on the App Store"
                  >
                    Android app on Google Play
                  </a>
                </li>
              </ul>
            </div>
          </ToggleMenuItems>
        </ToggleMenu>
      )}
      <ToggleButton onClick={() => setToggle(!toggle)}>
        <ToggleButtonLines>
          <ToggleButtonLine />
          <ToggleButtonLine />
          <ToggleButtonLine />
        </ToggleButtonLines>
      </ToggleButton>
    </>
  );
};

const HeaderNav = () => (
  <Media
    queries={{
      small: "(max-width: 599px)",
      medium: "(min-width: 600px) and (max-width: 1199px)",
      large: "(min-width: 1200px)"
    }}
  >
    {matches => (
      <>
        {matches.small && <ToggleNav />}
        {matches.medium && <Nav />}
        {matches.large && <Nav />}
      </>
    )}
  </Media>
);

export default HeaderNav;
