import React from "react";
import styled from "styled-components";

const Featured = styled.div`
  margin: 0 -20px;
  position: relative;
  width: calc(100% + 40px);
  @media (min-width: 620px) {
    margin: 0;
    width: 100%;
  }
`;
const Stories = () => (
  <div className="flex">
    <div className="w-full">
      <Featured>Stories</Featured>
    </div>
  </div>
);

export default Stories;
