import React, { useState, useEffect } from "react";
import styled from "styled-components";
import urlMetadata from "url-metadata";

const InfiniteStories = props => {
  const [ids, setIds] = useState([]);
  const [stories, setStories] = useState([]);
  const [visible, setVisible] = useState([]);
  const [loadMore, setLoadMore] = useState(false);
  const [lastId, setLastId] = useState(0);

  useEffect(() => {
    getStories();
  }, []);

  const getStories = () => {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    fetch("https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty")
      .then(res => {
        return !res.ok ? res.json().then(e => Promise.reject(e)) : res.json();
      })
      .then(res => {
        setIds(res);
        return res;
      })
      .then(ids =>
        ids.map(id =>
          fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)
            .then(res => {
              return !res.ok
                ? res.json().then(e => Promise.reject(e))
                : res.json();
            })
            .then(res => {
              urlMetadata(proxyurl + res.url).then(
                function(metadata) {
                  // success handler
                  console.log("Metada ", metadata["og:image"]);
                  const story = { ...res, image: metadata["og:image"] };
                  setStories(prevState => [...prevState, story]);
                },
                function(error) {
                  // failure handler
                  setStories(prevState => [...prevState, res]);
                }
              );
            })
            .catch(console.log)
        )
      )
      .catch(console.log);
  };

  useEffect(() => {
    //   setVisible(stories.slice(lastId, lastId + 30));
    loadMore && setLastId(lastId + 30);
    setLoadMore(false);
  }, [loadMore]);

  const LoadMoreDiv = styled.div`
    background: linear-gradient(hsla(0, 0%, 100%, 0), #f9f8f6);
    height: 390px;
    margin-top: -370px;
    pointer-events: none;
    position: relative;
    text-align: center;
    z-index: 2;
  `;

  const Button = styled.div`
    bottom: 0;
    left: 50%;
    margin-left: -90px;
    pointer-events: auto;
    position: absolute;
    width: 180px;
    font-family: FuturaBT-Book, sans-serif;
    font-size: 18px;
    font-weight: 300;
    background: #ffb21a;
    border-radius: 52px;
    color: #fff;
    padding: 16px 20px 15px;
    text-decoration: none;
    transition: background 80ms linear, opacity 1s linear;
    white-space: nowrap;
  `;

  console.log("LastId", lastId);
  return (
    <section className="mt-24">
      <div>
        <ul
          class="recent-posts js-load-more-posts-target js-macy-container"
          style="position: relative; height: 1284px;"
        >
          <li
            class="recent-posts-post"
            style="width: calc(50% - 10px); position: absolute; top: 0px; left: 0px;"
          >
            <a href="/en-ca/magazine/money-diary-sugar-ray-leonard">
              <div class="recent-post-image-container">
                <img src="https://wealthsimple-grow.ghost.io/content/images/2019/10/sugarrayleonard_finalartboard_1_copy_6-1.jpg" />
              </div>

              <div class="post-excerpt">
                <span class="post-category">Money Diaries</span>
                <h3 class="post-excerpt-title">
                  Boxing Legend “Sugar” Ray Leonard Was Never a Fighter
                </h3>
                <p>
                  He never wanted to be a professional boxer, he wanted to be a
                  substitute teacher. Until his father got sick. The money story
                  of the legendary five-time world champion, Olympic gold
                  medalist, and one of the greatest pound-for-pound fighters in
                  history.
                </p>
              </div>
            </a>
          </li>

          <li
            class="recent-posts-post"
            style="width: calc(50% - 10px); position: absolute; top: 0px; left: calc(50% + 10px);"
          >
            <a href="/en-ca/magazine/money-diary-matty-matheson">
              <div class="recent-post-image-container">
                <img src="https://wealthsimple-grow.ghost.io/content/images/2019/09/MattyM_final.jpg" />
              </div>

              <div class="post-excerpt">
                <span class="post-category">Money Diaries</span>
                <h3 class="post-excerpt-title">
                  The Path to Financial Freedom Is a Heart Attack: The Matty
                  Matheson Story
                </h3>
                <p>
                  He was 31 years old, an addict, and completely broke. How the
                  chef and host of Viceland’s ‘Deadset on Life,’ got his act
                  together and became a Toronto restaurant legend.
                </p>
              </div>
            </a>
          </li>

          <li
            class="recent-posts-post"
            style="width: calc(50% - 10px); position: absolute; top: 666px; left: 0px;"
          >
            <a href="/en-ca/magazine/money-diary-anastasia-soare">
              <div class="recent-post-image-container">
                <img src="https://wealthsimple-grow.ghost.io/content/images/2019/08/AnastasiaSoare_final_color-1.jpg" />
              </div>

              <div class="post-excerpt">
                <span class="post-category">Money Diaries</span>
                <h3 class="post-excerpt-title">
                  The Queen of the $3 Billion Eyebrow Empire
                </h3>
                <p>
                  Anastasia Soare arrived in America with, literally, zero
                  dollars. She believed in hard work, and that people should
                  care about the hair above their eyeballs. This is the story of
                  her and her eyebrows.
                </p>
              </div>
            </a>
          </li>

          <li
            class="recent-posts-post"
            style="width: calc(50% - 10px); position: absolute; top: 638px; left: calc(50% + 10px);"
          >
            <a href="/en-ca/magazine/money-diary-rachel-d-williams">
              <div class="recent-post-image-container">
                <img src="https://wealthsimple-grow.ghost.io/content/images/2019/08/Anna-Sorokin-Art-Final-1.jpg" />
              </div>

              <div class="post-excerpt">
                <span class="post-category">Money Diaries</span>
                <h3 class="post-excerpt-title">
                  How I was Conned by the “Fake German Heiress”
                </h3>
                <p>
                  Rachel DeLoache Williams’ relationship with Anna Delvey began
                  over drinks and infrared saunas, but it ended with a $60,000
                  bill, a grand jury, and a new understanding of friendship and
                  financial crime.
                </p>
              </div>
            </a>
          </li>
        </ul>
      </div>
      <LoadMoreDiv>
        <Button
          href="#"
          data-category="?category_id=money-diaries"
          onClick={() => setLoadMore(true)}
        >
          More posts
        </Button>
      </LoadMoreDiv>
    </section>
  );
};

export default InfiniteStories;
