import React, { useState, useEffect } from "react";
import styled from "styled-components";
import urlMetadata from "url-metadata";

const InfiniteStories = props => {
  const [pageSize, setPageSize] = useState(2);
  const [ids, setIds] = useState([]);
  const [stories, setStories] = useState([]);
  //   const [visible, setVisible] = useState([]);
  const [lastId, setLastId] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    getStories();
  }, []);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      if (!loading) {
        const list = document.getElementById("list");
        if (
          window.scrollY + window.innerHeight >=
          list.clientHeight + list.offsetTop
        ) {
          // New page has to be loaded
          let newLast = lastId + pageSize;
          setLastId(newLast);
          setLoading(true);
          newLast != 0 && getNextPage(ids);
        }
      }
    });
  }, [lastId]);

  const getStories = () => {
    return fetch(
      "https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty"
    )
      .then(res => {
        return !res.ok ? res.json().then(e => Promise.reject(e)) : res.json();
      })
      .then(res => {
        setIds(res);
        return res;
      })
      .then(ids => getNextPage(ids))
      .catch(console.log);
  };
  let loadedArticles = [];
  const getNextPage = ids => {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    var articlesLoaded = 0;
    loadedArticles = [];
    ids
      .filter((id, index, arr) => {
        return index >= lastId && index < lastId + pageSize;
      })
      .map(id =>
        fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)
          .then(res => {
            setLoading(++articlesLoaded < pageSize);
            return !res.ok
              ? res.json().then(e => Promise.reject(e))
              : res.json();
          })
          .then(res => {
            return urlMetadata(proxyurl + res.url).then(
              function(metadata) {
                // success handler
                const story = { ...res, image: metadata["og:image"] };
                loadedArticles.push(story);
              },
              function(error) {
                // failure handler
                // setStories(prevState => [...prevState, res]);
              }
            );
          })
          .then(() => {
              console.log("All articles loaded");
              setStories(prevState => [...prevState, loadedArticles]);
          })
          .catch(e => {
            console.log(e);
            // setLoading(false);
            setLoading(++articlesLoaded < pageSize);
            console.log("getNextpage error", articlesLoaded, loading);
          })
      );
  };

//   useEffect(() => {}, []);

  const LoadMoreDiv = styled.div`
    background: linear-gradient(hsla(0, 0%, 100%, 0), #f9f8f6);
    height: 390px;
    margin-top: -370px;
    pointer-events: none;
    position: relative;
    text-align: center;
    z-index: 2;
  `;

  const Button = styled.div`
    bottom: 0;
    left: 50%;
    margin-left: -90px;
    pointer-events: auto;
    position: absolute;
    width: 180px;
    font-family: FuturaBT-Book, sans-serif;
    font-size: 18px;
    font-weight: 300;
    background: #ffb21a;
    border-radius: 52px;
    color: #fff;
    padding: 16px 20px 15px;
    text-decoration: none;
    transition: background 80ms linear, opacity 1s linear;
    white-space: nowrap;
  `;

  const UL = styled.ul`
    position: relative;
    margin: 40px auto 0;
    width: 100%;
  `;

  const LI = styled.li`
    background: #f2efeb;
    margin-bottom: 20px;
    position: relative;
    width: 100%;
    width: calc(50% - 10px);
    float: left;
    margin-left: 10px;
    &:nth-child(odd) {
      clear: both;
      display: table;
    }
  `;

  const Anchor = styled.a`
    color: inherit;
    text-decoration: none;
  `;

  const ArticleContainer = styled.div`
    display: block;
    height: 0;
    overflow: hidden;
    padding-bottom: 60%;
    position: relative;
    width: 100%;
  `;

  const ArticleImage = styled.img`
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
    float: left;
    width: 100%;
  `;

  const PostExcerpt = styled.div`
    background: #fff;
    clear: both;
    line-height: 28px;
    padding: 30px 30px 15px;
  `;

  const PostExcerptTitle = styled.h3`
    font-family: FuturaBT-Heavy, sans-serif;
    font-size: inherit;
    font-weight: 100;
    font-size: 24px;
    letter-spacing: -0.03em;
    line-height: 32px;
    margin: 10px 0 20px;
  `;

  const PostCategory = styled.span`
    font-family: FuturaBT-Heavy, sans-serif;
    font-size: 14px;
    font-weight: 100;
    letter-spacing: 2px;
    opacity: 0.4;
    text-transform: uppercase;
  `;
  return (
    <section className="mt-24">
      <div>
        <UL id="list">
          {stories.map((story, index) => (
            <LI key={index}>
              <Anchor href={story.url}>
                <ArticleContainer>
                  <ArticleImage alt="1" src={story.image} />
                </ArticleContainer>

                <PostExcerpt className="post-excerpt">
                  <PostCategory>Money Diaries</PostCategory>
                  <PostExcerptTitle className="post-excerpt-title">
                    {story.title}
                  </PostExcerptTitle>
                </PostExcerpt>
              </Anchor>
            </LI>
          ))}
          <li style={{ clear: "both" }}></li>
        </UL>
      </div>
      <div style={{ clear: "both" }}></div>
      <LoadMoreDiv>
        <Button
          href="#"
          data-category="?category_id=money-diaries"
          onClick={() => {
            setLastId(p => p + pageSize);
          }}
        >
          More posts
        </Button>
      </LoadMoreDiv>
    </section>
  );
};

export default InfiniteStories;
