import React, { useState, useEffect } from "react";
import styled from "styled-components";
import urlMetadata from "url-metadata";

const InfiniteStories = props => {
  const [ids, setIds] = useState([]);
  const [stories, setStories] = useState([]);
  const [visible, setVisible] = useState([]);
  const [loadMore, setLoadMore] = useState(false);
  const [lastId, setLastId] = useState(0);

  useEffect(() => {
    getStories();
  }, []);

  const getStories = () => {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    fetch("https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty")
      .then(res => {
        return !res.ok ? res.json().then(e => Promise.reject(e)) : res.json();
      })
      .then(res => {
        setIds(res);
        return res;
      })
      .then(ids =>
        ids.map(id =>
          fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)
            .then(res => {
              return !res.ok
                ? res.json().then(e => Promise.reject(e))
                : res.json();
            })
            .then(res => {
              //   urlMetadata(proxyurl + res.url).then(
              //     function(metadata) {
              //       // success handler
              //       console.log("Metada ", metadata["og:image"]);
              //       const story = { ...res, image: metadata["og:image"] };
              //       setStories(prevState => [...prevState, story]);
              //     },
              //     function(error) {
              //       // failure handler
              //       setStories(prevState => [...prevState, res]);
              //     }
              //   );
              setStories(prevState => [...prevState, res]);
            })
            .catch(console.log)
        )
      )
      .catch(console.log);
  };

  useEffect(() => {
    //   setVisible(stories.slice(lastId, lastId + 30));
    loadMore && setLastId(lastId + 30);
    setLoadMore(false);
  }, [loadMore]);

  const LoadMoreDiv = styled.div`
    background: linear-gradient(hsla(0, 0%, 100%, 0), #f9f8f6);
    height: 390px;
    margin-top: -370px;
    pointer-events: none;
    position: relative;
    text-align: center;
    z-index: 2;
  `;

  const Button = styled.div`
    bottom: 0;
    left: 50%;
    margin-left: -90px;
    pointer-events: auto;
    position: absolute;
    width: 180px;
    font-family: FuturaBT-Book, sans-serif;
    font-size: 18px;
    font-weight: 300;
    background: #ffb21a;
    border-radius: 52px;
    color: #fff;
    padding: 16px 20px 15px;
    text-decoration: none;
    transition: background 80ms linear, opacity 1s linear;
    white-space: nowrap;
  `;

  const UL = styled.ul`
    position: relative;
    height: 1284px;
    margin: 40px auto 0;
    width: 100%;
  `;

  const LI = styled.li`
    width: calc(50% - 10px);
    position: absolute;
    top: 0px;
    left: 0px;
    background: #f2efeb;
    margin-bottom: 20px;
    position: relative;
    width: 100%;
  `;

  const Anchor = styled.a`
    color: inherit;
    text-decoration: none;
  `;
  console.log("LastId", lastId);
  const visibleStories = stories.slice(lastId, lastId + 30);
  return (
    <section className="mt-24">
      <div>
        {visibleStories.map((story, index) => (
          <UL key={index}>
            <LI>
              <Anchor href={story.url}>
                <div className="recent-post-image-container">
                  <img src="https://s2.reutersmedia.net/resources/r/?m=02&d=20191013&t=2&i=1440326434&w=1200&r=LYNXMPEF9C0AH" />
                </div>

                <div class="post-excerpt">
                  <h3 class="post-excerpt-title">{story.title}</h3>
                  <p>
                    He never wanted to be a professional boxer, he wanted to be
                    a substitute teacher. Until his father got sick. The money
                    story of the legendary five-time world champion, Olympic
                    gold medalist, and one of the greatest pound-for-pound
                    fighters in history.
                  </p>
                </div>
              </Anchor>
            </LI>
          </UL>
        ))}
      </div>
      <LoadMoreDiv>
        <Button
          href="#"
          data-category="?category_id=money-diaries"
          onClick={() => setLoadMore(true)}
        >
          More posts
        </Button>
      </LoadMoreDiv>
    </section>
  );
};

export default InfiniteStories;
