import React, { useState, useEffect } from "react";

const InfiniteStories = props => {
  const [ids, setIds] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [searchable, setSearchable] = useState([]);
  const [loadMore, setLoadMore] = useState(false);
  const [lastId, setLastId] = useState(0);
  useEffect(() => {
    getIds();
  }, []);
  useEffect(() => {
    loaded && getData();
    setLoadMore(false);
  }, [loadMore]);
  const getIds = () => {
    fetch("https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty")
      .then(res => {
        return !res.ok ? res.json().then(e => Promise.reject(e)) : res.json();
      })
      .then(res => {
        setIds(res);
        setSearchable(ids.slice(lastId, 30));
        setLoaded(true);
      })
      .catch(console.log);
  };
  const getData = () => {
    console.log("Searchable Ids", searchable);
    // fetch("https://dog.ceo/api/breeds/image/random/15")
    //   .then(res => {
    //     return !res.ok ? res.json().then(e => Promise.reject(e)) : res.json();
    //   })
    //   .then(res => props.setState([...props.state, ...res.message]))
    //   .catch(console.log);
  };
  return <div>Hello</div>;
};

export default InfiniteStories;
