import React from "react";
import styled from "styled-components";
import { FooterLinks, Icons } from "./Constants";

const Container = styled.footer.attrs({ className: "w-full" })`
  background: #222;
  margin: 120px 0 0;

  @media (min-width: 620px) {
    padding: 40px 0 60px;
  }

  @media (min-width: 1020px) {
    padding: 50px 0 100px;
  }

  @media (max-width: 619px) {
    margin-top: 0;
  }
`;

const UpperBlock = styled.div.attrs({ className: "w-full" })`
  margin-left: auto;
  margin-right: auto;
  max-width: 1490px;
  padding-left: 25px;
  padding-right: 25px;
  padding-top: 50px;

  @media (min-width: 620px) {
    max-width: 1600px;
    padding-left: 80px;
    padding-right: 80px;
  }

  @media (min-width: 1020px) {
    max-width: 1684px;
    padding-left: 122px;
    padding-right: 122px;
  }
`;

const FooterContent = styled.div.attrs({
  className: "text-white my-0 mx-auto"
})`
  &::after {
    clear: both;
    content: "";
    display: table;
  }
  max-width: 1200px;
`;

const FooterListWrapper = styled.div.attrs({ className: "" })`
  margin: 0 0 50px;
  @media (min-width: 620px) {
    float: left;
    width: 33%;
    ${props => props.social && `width: 100%;`}
  }

  @media (min-width: 1020px) {
    float: left;
    width: 25%;
  }
`;

const FootList = styled.ul`
  text-align: left;
  vertical-align: top;
`;

const NameLi = styled.li`
  font-family: FuturaBT-Heavy, sans-serif;
  font-size: 14px;
  font-weight: 100;
  letter-spacing: 2px;
  text-transform: uppercase;
  margin-bottom: 25px;
`;

const Li = styled.li`
  font-size: 18px;
  font-family: FuturaBT-Book, sans-serif;
  font-weight: 300;
  margin-bottom: 10px;
`;

const FooterLink = styled.a`
  color: #fff;
  opacity: 1;
  text-decoration: none;
  transition: opacity 60ms linear;
`;
const FooterIcon = styled.a`
  background: url(${props => props.url}) no-repeat;
  display: block;
  height: 35px;
  overflow: hidden;
  text-indent: -9999em;
  width: 28px;
`;
const Footer = () => (
  <Container>
    <UpperBlock>
      <FooterContent>
        {FooterLinks.map((item, index) => (
          <FooterListWrapper key={index}>
            <FootList>
              <NameLi> {item.name} </NameLi>
              {item.links.map((link, index) => (
                <Li key={index}>
                  <FooterLink href={link.href}>{link.name}</FooterLink>
                </Li>
              ))}
            </FootList>
          </FooterListWrapper>
        ))}
        {Icons.map((item, index) => (
          <FooterListWrapper key={index}>
            <FootList>
              <NameLi>{item.name}</NameLi>
              {item.links.map((link, index) => (
                <Li key={index}>
                  <FooterIcon href={link.href} url={link.url} />
                </Li>
              ))}
            </FootList>
          </FooterListWrapper>
        ))}
      </FooterContent>
    </UpperBlock>
  </Container>
);

export default Footer;
